// Custom script
// --------------------------------------------------


//= Global

var global = global || {};

$.fn.exists = function() {
return this.length > 0;
}


$.fn.modal = function () {
 this.on('click', function (e) {
  e.preventDefault()
   $('#modal-reservation').toggleClass('modal-open', !$('#modal-reservation').hasClass('modal-open'))
   $('.js-datepicker').datepick()
 })
}

$.fn.datepick = function () {
if ($(this).exists()) {
$(this).datepicker({ format: "mm/dd/yy" })
}
}


//= Initialize

$.fn.initialize = function() {
 $('.js-modal').modal()
 $('.js-datepicker').datepicker()
}


//= Document ready

$(function() {
/* Common */

/* First init */
$('body').removeClass('no-js').addClass('js').initialize();

/* Events */
});